function decodeUplink(input) {
  // Verificar que el payload decodificado existe
  if (
    !input.uplink_message ||
    !input.uplink_message.decoded_payload ||
    typeof input.uplink_message.decoded_payload !== 'object'
  ) {
    return { errors: ["No decoded payload found in input"] };
  }

  const payload = input.uplink_message.decoded_payload;
  const metadata = input.uplink_message;
  const deviceInfo = input.end_device_ids;

  payload["battery_percent"] = metadata.last_battery_percentage?.value ?? null;
  payload["device_id"] = deviceInfo.device_id;
  payload["source"] = input.source; // Injected from TTN or LORIOT plugin

  return payload;
}

function prepareDownlink(payload) {

  console.log("PPPBDA " + JSON.stringify(payload));
  let result = {
    data: hexToBase64(payload.payload["Message"]),
    device_id: payload["device_id"],
    port: 2,
    priority: payload.payload["Priority"],
    replace_downlink: payload.payload["Replace_downlink"],
    confirmed: payload.payload["Confirmed"]
  };

  return result;
}

function hexToBase64(hexStr) {
    const hex = hexStr.replace(/\s+/g, '');
    const bytes = [];
    for(let i = 0; i < hex.length; i += 2) {
        bytes.push(parseInt(hex.substr(i, 2), 16));
    }
    return Buffer.from(bytes).toString('base64');
}
