function decodeUplink(uplink) {
  //print(JSON.stringify(uplink));
  if (!uplink || typeof uplink !== 'object') return uplink;

  // If already decoded, just return
  if (uplink.decoded_payload != null) return uplink;

  const hex = uplink.payload;
  if (!hex || typeof hex !== 'string') return uplink;

  let bytes;
  try {
    bytes = hexToBytes(hex);
  } catch (e) {
    return uplink;
  }

  print("MARAMBA" + bytes);

  let res;
  res = decodeLt22222L(bytes);


  if (res && res.data) {
    return { ...uplink, decodedPayload: res.data };
  }

  return uplink;
}


/* ---------- helpers ---------- */

function hexToBytes(hex) {
  const clean = hex.trim().replace(/^0x/i, '');
  if (clean.length % 2 !== 0) throw new Error('hex length must be even');
  const out = [];
  for (let i = 0; i < clean.length; i += 2) {
    const b = parseInt(clean.slice(i, i + 2), 16);
    if (Number.isNaN(b)) throw new Error('invalid hex byte at ' + i);
    out.push(b);
  }
  return out;
}

function u32be(b, i) {
  return ((b[i] << 24) | (b[i + 1] << 16) | (b[i + 2] << 8) | b[i + 3]) >>> 0;
}
function u16be(b, i) {
  return ((b[i] << 8) | b[i + 1]) >>> 0;
}

function decodeLt22222L(bytes) {
  if (!bytes || !Array.isArray(bytes)) {
    return { errors: ['bytes must be an array'] };
  }
  if (bytes.length < 11) {
    return { errors: ['payload too short for LT22222L data (need >=11 bytes)'] };
  }

  // Helpers (unsigned)
  const u16 = (hi, lo) => ((hi << 8) | lo) & 0xFFFF;
  const u32 = (b0,b1,b2,b3) =>
    (((b0 << 24) >>> 0) + (b1 << 16) + (b2 << 8) + b3) >>> 0;

  const modeByte = bytes[10] & 0xFF;
  const mode = modeByte & 0x3F;          // 0..63

  const data = { hardwareMode: 'LT22222L', mode };

  if (mode !== 6) {
    data.DO1_status = (bytes[8] & 0x01) ? 'L' : 'H';
    data.DO2_status = (bytes[8] & 0x02) ? 'L' : 'H';
    data.RO1_status = (bytes[8] & 0x80) ? 'ON' : 'OFF';
    data.RO2_status = (bytes[8] & 0x40) ? 'ON' : 'OFF';
    if (mode !== 1) {
      if (mode !== 5) {
        data.Count1_times = u32(bytes[0], bytes[1], bytes[2], bytes[3]);
      }
      data.First_status = (bytes[8] & 0x20) ? 'Yes' : 'No';
    }
  }

  if (mode === 1) {
    data.Work_mode  = '2ACI+2AVI';
    data.AVI1_V     = +(u16(bytes[0], bytes[1]) / 1000).toFixed(3);
    data.AVI2_V     = +(u16(bytes[2], bytes[3]) / 1000).toFixed(3);
    data.ACI1_mA    = +(u16(bytes[4], bytes[5]) / 1000).toFixed(3);
    data.ACI2_mA    = +(u16(bytes[6], bytes[7]) / 1000).toFixed(3);
    data.DI1_status = (bytes[8] & 0x08) ? 'H' : 'L';
    data.DI2_status = (bytes[8] & 0x10) ? 'H' : 'L';

  } else if (mode === 2) {
    data.Work_mode     = 'Count mode 1';
    data.Count2_times  = u32(bytes[4], bytes[5], bytes[6], bytes[7]);

  } else if (mode === 3) {
    data.Work_mode  = '2ACI+1Count';
    data.ACI1_mA    = +(u16(bytes[4], bytes[5]) / 1000).toFixed(3);
    data.ACI2_mA    = +(u16(bytes[6], bytes[7]) / 1000).toFixed(3);

  } else if (mode === 4) {
    data.Work_mode    = 'Count mode 2';
    data.Acount_times = u32(bytes[4], bytes[5], bytes[6], bytes[7]);

  } else if (mode === 5) {
    data.Work_mode  = '1ACI+2AVI+1Count';
    data.AVI1_V     = +(u16(bytes[0], bytes[1]) / 1000).toFixed(3);
    data.AVI2_V     = +(u16(bytes[2], bytes[3]) / 1000).toFixed(3);
    data.ACI1_mA    = +(u16(bytes[4], bytes[5]) / 1000).toFixed(3);
    data.Count1_times = ((bytes[6] << 8) | bytes[7]) & 0xFFFF; // si es 16‑bit aquí

  } else if (mode === 6) {
    data.Work_mode  = 'Exit mode';
    data.Mode_status = !!bytes[9];
    data.AV1L_flag = !!(bytes[0] & 0x80);
    data.AV1H_flag = !!(bytes[0] & 0x40);
    data.AV2L_flag = !!(bytes[0] & 0x20);
    data.AV2H_flag = !!(bytes[0] & 0x10);
    data.AC1L_flag = !!(bytes[0] & 0x08);
    data.AC1H_flag = !!(bytes[0] & 0x04);
    data.AC2L_flag = !!(bytes[0] & 0x02);
    data.AC2H_flag = !!(bytes[0] & 0x01);

    data.AV1L_status = !!(bytes[1] & 0x80);
    data.AV1H_status = !!(bytes[1] & 0x40);
    data.AV2L_status = !!(bytes[1] & 0x20);
    data.AV2H_status = !!(bytes[1] & 0x10);
    data.AC1L_status = !!(bytes[1] & 0x08);
    data.AC1H_status = !!(bytes[1] & 0x04);
    data.AC2L_status = !!(bytes[1] & 0x02);
    data.AC2H_status = !!(bytes[1] & 0x01);

    data.DI2_status = !!(bytes[2] & 0x08);
    data.DI2_flag   = !!(bytes[2] & 0x04);
    data.DI1_status = !!(bytes[2] & 0x02);
    data.DI1_flag   = !!(bytes[2] & 0x01);
  }

  return { data };
}


