properties.options:
var options = {
    series: series,
    chart: {
        type: 'area',
        background: '#424242',
        toolbar: {
            show: true,
            tools: {
                download: true,
                selection: true,
                zoom: true,
                zoomin: true,
                zoomout: true,
                pan: true,
                reset: true
            },
            autoSelected: 'zoom'
        },
        zoom: {
            enabled: true,
            type: 'x',
            autoScaleYaxis: true
        }
    },
    stroke: {
        curve: 'smooth',
        width: 2
    },
    fill: {
        type: 'gradient',
        gradient: {
            shadeIntensity: 1,
            opacityFrom: 0.7,
            opacityTo: 0.3
        }
    },
    grid: {
        row: {
            colors: ['#5a5a5a', 'transparent'],
            opacity: 0.3
        }
    },
    xaxis: {
        type: 'datetime',
        labels: {
            datetimeUTC: false
        }
    },
    yaxis: {
        labels: {
            formatter: function(val) {
                return val.toFixed(2) + ' kWh';
            },
            style: { colors: '#FFFFFF' }
        },
        title: {
            text: 'Energy (kWh)',
            style: { color: '#FFFFFF' }
        }
    },
    tooltip: {
        x: { format: 'dd/MM/yyyy HH:mm' },
        shared: true
    },
    legend: {
        position: 'top',
        labels: { colors: '#FFFFFF' }
    }
};
