properties.options:
var options = {
    series: series,
    chart: {
        background: '#1976D2',
        toolbar: {
            show: true,
            tools: {
                download: true,
                selection: true,
                zoom: true,
                zoomin: true,
                zoomout: true,
                pan: true,
                reset: true
            },
            autoSelected: 'zoom'
        },
        zoom: {
            enabled: true,
            type: 'x',
            autoScaleYaxis: true
        }
    },
    stroke: {
        curve: 'smooth',
        width: 2
    },
    grid: {
        row: {
            colors: ['#1E88E5', 'transparent'],
            opacity: 0.3
        }
    },
    xaxis: {
        type: 'datetime',
        labels: {
            datetimeUTC: false
        }
    },
    yaxis: [
        {
            seriesName: 'Temperature',
            title: { text: 'Temperature (°C)', style: { color: '#FF5722' } },
            labels: {
                formatter: function(val) { return val.toFixed(1) + '°C'; },
                style: { colors: '#FFFFFF' }
            }
        },
        {
            opposite: true,
            seriesName: 'Humidity',
            title: { text: 'Humidity (%)', style: { color: '#4CAF50' } },
            labels: {
                formatter: function(val) { return val.toFixed(1) + '%'; },
                style: { colors: '#FFFFFF' }
            }
        }
    ],
    tooltip: {
        x: { format: 'dd/MM/yyyy HH:mm' },
        shared: true
    },
    legend: {
        position: 'bottom',
        labels: { colors: '#FFFFFF' }
    }
};
