function decodeThingerUplink(thingerData) {
    // 0. If data has already been decoded, we will return it
    if (thingerData.decodedPayload) return thingerData.decodedPayload;

    // 1. Extract and Validate Input
    // We need 'payload' (hex string) and 'fPort' (integer)
    const hexPayload = thingerData.payload || "";
    const port = thingerData.fPort || 1;

    // 2. Convert Hex String to Byte Array
    const bytes = [];
    for (let i = 0; i < hexPayload.length; i += 2) {
        bytes.push(parseInt(hexPayload.substr(i, 2), 16));
    }

    // 3. Dynamic Function Detection and Execution

    // CASE A: (The Things Stack v3)
    if (typeof decodeUplink === 'function') {
        try {
            const input = {
                bytes: bytes,
                fPort: port
            };
            var result = decodeUplink(input);

            if (result.data) return result.data;

            return result; 
        } catch (e) {
            console.error("Error inside decodeUplink:", e);
            throw e;
        }
    }

    // CASE B: Legacy TTN (v2)
    else if (typeof Decoder === 'function') {
        try {
            return Decoder(bytes, port);
        } catch (e) {
            console.error("Error inside Decoder:", e);
            throw e;
        }
    }

    // CASE C: No decoder found
    else {
        throw new Error("No compatible TTN decoder function (decodeUplink or Decoder) found in scope.");
    }
}


// TTN decoder
function decodeUplink(input) {
  var data = {};

  switch (input.fPort) {
    case 1:

     if (input.bytes[4] === 0x00){
       data.trapState = 'failed';
     } else if (input.bytes[4] === 0x01){
       data.trapState = 'normal';
     } else if (input.bytes[4] === 0x02){
      data.trapState = 'trapped';
    } else if (input.bytes[4] === 0x03){
      data.trapState = 'abnormal';
    } else if (input.bytes[4] === 0x04){
      data.trapState = 'moved';
    } else if (input.bytes[4] === 0x07){
      data.trapState = 'error';
    } else if (input.bytes[4] === 0x08){
      data.trapState = 'wakeup';
    }
    data.msgId = input.bytes[0];
    data.battVoltage = input.bytes[1]/10;
    data.temperature = (input.bytes[2] << 8 | input.bytes[3]) / 100;
    data.id = input.bytes[10] << 8 | input.bytes[5];




      return {
        data : data,
      };
  }
}

